/* eslint-disable import/no-anonymous-default-export */
export default (posts = [], action) => {
    switch (action.type){
        case 'UPDATE':
            return posts.map()
        case 'FETCH_ALL':
            return action.payload
        case 'CREATE': 
            return [...posts, action.payload]
        default:
            return posts 
    }
}
