import * as api from '../api'

const getPosts = () => async (dispatch) => {

    try{
        const {data} = await api.fetchPosts()

        dispatch({type: 'FETCH_ALL', payload: data})
    } catch(err){
        console.log(ErrorEvent)
    }


    const action = {type: 'FETCH_ALL', payload: data} 

    dispatch(action)
}
